from subprocess import Popenfrom os import makedirsdef extract(lzs):    out = ".\\_arc\\" + lzs.split(".")[0]    Popen("quickbms.exe namco_sszl.bms " + lzs + " " + out)def unpack(arc):    makedirs(".\\_unpack\\" + arc.split(".")[0], exist_ok = True)    out = ".\\_unpacked\\" + arc.split(".")[0]    Popen("quickbms.exe namco_vcra.bms " + arc + " " + out)# extract("resource.lzs")# unpack(".\\_arc\\resource.arc")